"use client";

import { useEffect, useRef } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "@maptiler/geocoding-control/dist/index.css";
import { GeocodingControl } from "@maptiler/geocoding-control";

const Map = ({ pickup, destination, onLocationSelect }) => {
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const markersRef = useRef([]);
  const routeRef = useRef(null);

  useEffect(() => {
    if (!mapInstanceRef.current) {
      initMap();
    }
  }, []);

  useEffect(() => {
    if (mapInstanceRef.current) {
      // Clear existing markers
      markersRef.current.forEach(marker => marker.remove());
      markersRef.current = [];

      // Add markers for pickup and destination if they exist
      if (pickup) {
        addMarker(pickup, "Pickup Location", "green");
      }
      if (destination) {
        addMarker(destination, "Destination", "red");
      }

      // Draw route if both points exist
      if (pickup && destination) {
        drawRoute(pickup, destination);
      }
    }
  }, [pickup, destination]);

  const initMap = () => {
    const defaultLocation = [20.5937, 78.9629]; // Center of India

    mapInstanceRef.current = L.map(mapRef.current).setView(defaultLocation, 12);

    L.tileLayer(
      "https://api.maptiler.com/maps/streets-v2/{z}/{x}/{y}.png?key=uY3F88mWlOJKH9sntYdw",
      {
        attribution: "&copy; MapTiler & OpenStreetMap contributors",
      }
    ).addTo(mapInstanceRef.current);

    // Add geocoding (autocomplete) control
    const geocodingControl = new GeocodingControl({
      apiKey: "uY3F88mWlOJKH9sntYdw",
      placeholder: "Search location...",
    });

    geocodingControl.addTo(mapInstanceRef.current);

    geocodingControl.on("select", (e) => {
      const { lat, lon } = e.detail.center;
      if (!pickup) {
        onLocationSelect("pickup", { lat, lng: lon });
      } else {
        onLocationSelect("destination", { lat, lng: lon });
      }
    });

    // Try to get user location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const userLocation = [position.coords.latitude, position.coords.longitude];
          mapInstanceRef.current.setView(userLocation, 14);
          addMarker({ lat: userLocation[0], lng: userLocation[1] }, "Your Location", "blue");
        },
        () => console.log("Geolocation failed."),
      );
    }
  };

  const addMarker = (location, title, color) => {
    const icon = L.icon({
      iconUrl: `https://maptiler.com/img/markers/${color}-marker.png`,
      iconSize: [25, 41],
      iconAnchor: [12, 41],
    });

    const marker = L.marker([location.lat, location.lng], { icon }).addTo(mapInstanceRef.current).bindPopup(title);
    markersRef.current.push(marker);
  };

  const drawRoute = (origin, destination) => {
    if (routeRef.current) {
      mapInstanceRef.current.removeLayer(routeRef.current);
    }

    fetch(
      `https://api.maptiler.com/routing/directions/driving/${origin.lng},${origin.lat};${destination.lng},${destination.lat}?key=uY3F88mWlOJKH9sntYdw&geometries=geojson`
    )
      .then((response) => response.json())
      .then((data) => {
        if (data.routes && data.routes.length > 0) {
          const routeCoords = data.routes[0].geometry.coordinates.map(coord => [coord[1], coord[0]]);
          routeRef.current = L.polyline(routeCoords, { color: "blue", weight: 5 }).addTo(mapInstanceRef.current);
        }
      })
      .catch(error => console.error("Error fetching route:", error));
  };

  return <div ref={mapRef} className="map-container" style={{ height: "400px" }}></div>;
};

export default Map;
