"use client"

import { useState, useEffect } from "react"
import { useParams } from "react-router-dom"
import axios from "axios"
import Map from "../components/Map"

const RideDetails = () => {
  const { id } = useParams()
  const [booking, setBooking] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")

  useEffect(() => {
    const fetchBookingDetails = async () => {
      try {
        const res = await axios.get(`/api/bookings/${id}`)
        setBooking(res.data)
        setLoading(false)
      } catch (err) {
        console.error("Error fetching booking details:", err)
        setError("Failed to load booking details")
        setLoading(false)
      }
    }

    fetchBookingDetails()

    // Set up polling for status updates
    const intervalId = setInterval(fetchBookingDetails, 10000) // Poll every 10 seconds

    return () => clearInterval(intervalId) // Clean up on unmount
  }, [id])

  if (loading) {
    return <div>Loading ride details...</div>
  }

  if (error) {
    return <div className="alert alert-danger">{error}</div>
  }

  if (!booking) {
    return <div className="alert alert-danger">Booking not found</div>
  }

  const getStatusClass = (status) => {
    switch (status) {
      case "confirmed":
        return "status-confirmed"
      case "in-progress":
        return "status-in-progress"
      case "completed":
        return "status-completed"
      default:
        return ""
    }
  }

  return (
    <div className="ride-details-container">
      <h2 className="form-title">Ride Details</h2>

      <div className="ride-status-container">
        <span className={`ride-status ${getStatusClass(booking.status)}`}>
          {booking.status === "confirmed" && "Confirmed - Driver on the way"}
          {booking.status === "in-progress" && "In Progress"}
          {booking.status === "completed" && "Completed"}
        </span>
      </div>

      <Map pickup={booking.pickup.coordinates} destination={booking.destination.coordinates} />

      <div className="ride-details">
        <div className="driver-info">
          <img
            src={booking.driver?.photo || "/placeholder.svg?height=60&width=60"}
            alt="Driver"
            className="driver-photo"
          />
          <div>
            <p className="driver-name">{booking.driver?.name || "Driver not assigned yet"}</p>
            {booking.driver && (
              <p className="vehicle-info">
                {booking.driver.vehicleModel} - {booking.driver.vehicleNumber}
              </p>
            )}
          </div>
        </div>

        <div className="trip-details">
          <h3>Trip Details</h3>
          <div className="detail-row">
            <span>Pickup:</span>
            <span>{booking.pickup.address}</span>
          </div>
          <div className="detail-row">
            <span>Destination:</span>
            <span>{booking.destination.address}</span>
          </div>
          <div className="detail-row">
            <span>Distance:</span>
            <span>{booking.distance.toFixed(2)} km</span>
          </div>
          <div className="detail-row">
            <span>Duration:</span>
            <span>{booking.duration} minutes</span>
          </div>
          <div className="detail-row">
            <span>Cab Type:</span>
            <span>{booking.cabType.name}</span>
          </div>
          <div className="detail-row">
            <span>Fare:</span>
            <span>₹{booking.fare}</span>
          </div>
          <div className="detail-row">
            <span>Payment Method:</span>
            <span>{booking.paymentMethod || "Cash"}</span>
          </div>
          <div className="detail-row">
            <span>Booking Time:</span>
            <span>{new Date(booking.createdAt).toLocaleString()}</span>
          </div>
        </div>

        {booking.status === "completed" && (
          <div className="rating-section">
            <h3>Rate Your Ride</h3>
            <div className="star-rating">
              {[1, 2, 3, 4, 5].map((star) => (
                <span key={star} className="star">
                  ★
                </span>
              ))}
            </div>
            <textarea className="form-control" placeholder="Share your feedback (optional)"></textarea>
            <button className="btn btn-primary">Submit Rating</button>
          </div>
        )}
      </div>
    </div>
  )
}

export default RideDetails
