"use client"

import { useState, useEffect } from "react"
import { useParams } from "react-router-dom"

const RideDetails = () => {
  const { id } = useParams()
  const [booking, setBooking] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")

  useEffect(() => {
    // Get booking data from sessionStorage
    const fetchBookingDetails = () => {
      try {
        const storedBooking = sessionStorage.getItem("currentBooking")

        if (storedBooking) {
          const bookingData = JSON.parse(storedBooking)

          // Create a complete booking object with the stored data
          const completeBooking = {
            _id: id,
            status: "confirmed",
            pickup: bookingData.pickup,
            destination: bookingData.destination,
            distance: bookingData.distance,
            duration: bookingData.duration,
            fare: bookingData.fare,
            cabType: {
              name: getCabNameById(bookingData.cabType),
            },
            driver: {
              name: "Rahul Kumar",
              photo: "/placeholder.svg",
              vehicleModel: "Maruti Swift",
              vehicleNumber: "DL-01-AB-1234",
              phone: "9876543210",
            },
            paymentMethod: "Cash",
            createdAt: new Date().toISOString(),
          }

          setBooking(completeBooking)
          setLoading(false)
        } else {
          // If no stored booking, create a sample one
          setTimeout(() => {
            const sampleBooking = {
              _id: id,
              status: "confirmed",
              pickup: {
                coordinates: { lat: 28.6139, lng: 77.209 },
                address: "Delhi, Delhi",
              },
              destination: {
                coordinates: { lat: 19.076, lng: 72.8777 },
                address: "Mumbai, Maharashtra",
              },
              distance: 1421.5,
              duration: 1260, // 21 hours in minutes
              fare: 15800,
              cabType: { name: "Premium" },
              driver: {
                name: "Rahul Kumar",
                photo: "/placeholder.svg",
                vehicleModel: "Maruti Swift",
                vehicleNumber: "DL-01-AB-1234",
                phone: "9876543210",
              },
              paymentMethod: "Cash",
              createdAt: new Date().toISOString(),
            }

            setBooking(sampleBooking)
            setLoading(false)
          }, 1000)
        }
      } catch (err) {
        console.error("Error fetching booking details:", err)
        setError("Failed to load booking details")
        setLoading(false)
      }
    }

    fetchBookingDetails()
  }, [id])

  // Helper function to get cab name from id
  const getCabNameById = (cabId) => {
    switch (cabId) {
      case "1":
        return "Economy"
      case "2":
        return "Premium"
      case "3":
        return "SUV"
      default:
        return "Standard"
    }
  }

  if (loading) {
    return <div>Loading ride details...</div>
  }

  if (error) {
    return <div className="alert alert-danger">{error}</div>
  }

  if (!booking) {
    return <div className="alert alert-danger">Booking not found</div>
  }

  const getStatusClass = (status) => {
    switch (status) {
      case "confirmed":
        return "status-confirmed"
      case "in-progress":
        return "status-in-progress"
      case "completed":
        return "status-completed"
      default:
        return ""
    }
  }

  // Format duration to hours and minutes
  const formatDuration = (minutes) => {
    const hours = Math.floor(minutes / 60)
    const mins = minutes % 60

    if (hours > 0) {
      return `${hours} hour${hours > 1 ? "s" : ""} ${mins > 0 ? `${mins} min` : ""}`
    }
    return `${mins} minutes`
  }

  return (
    <div className="ride-details-container">
      <h2 className="form-title">Ride Details</h2>

      <div className="ride-status-container">
        <span className={`ride-status ${getStatusClass(booking.status)}`}>
          {booking.status === "confirmed" && "Confirmed - Driver on the way"}
          {booking.status === "in-progress" && "In Progress"}
          {booking.status === "completed" && "Completed"}
        </span>
      </div>

      <div className="ride-details">
        <div className="driver-info">
          <img
            src={booking.driver?.photo || "/placeholder.svg"}
            alt="Driver"
            className="driver-photo"
            width="60"
            height="60"
          />
          <div>
            <p className="driver-name">{booking.driver?.name || "Driver not assigned yet"}</p>
            {booking.driver && (
              <p className="vehicle-info">
                {booking.driver.vehicleModel} - {booking.driver.vehicleNumber}
              </p>
            )}
          </div>
        </div>

        <div className="trip-details">
          <h3>Trip Details</h3>
          <div className="detail-row">
            <span>Pickup:</span>
            <span>{booking.pickup.address}</span>
          </div>
          <div className="detail-row">
            <span>Destination:</span>
            <span>{booking.destination.address}</span>
          </div>
          <div className="detail-row">
            <span>Distance:</span>
            <span>{booking.distance.toFixed(2)} km</span>
          </div>
          <div className="detail-row">
            <span>Duration:</span>
            <span>{formatDuration(booking.duration)}</span>
          </div>
          <div className="detail-row">
            <span>Cab Type:</span>
            <span>{booking.cabType.name}</span>
          </div>
          <div className="detail-row">
            <span>Fare:</span>
            <span>₹{booking.fare}</span>
          </div>
          <div className="detail-row">
            <span>Payment Method:</span>
            <span>{booking.paymentMethod || "Cash"}</span>
          </div>
          <div className="detail-row">
            <span>Booking Time:</span>
            <span>{new Date(booking.createdAt).toLocaleString()}</span>
          </div>
        </div>

        {booking.status === "completed" && (
          <div className="rating-section">
            <h3>Rate Your Ride</h3>
            <div className="star-rating">
              {[1, 2, 3, 4, 5].map((star) => (
                <span key={star} className="star">
                  ★
                </span>
              ))}
            </div>
            <textarea className="form-control" placeholder="Share your feedback (optional)"></textarea>
            <button className="btn btn-primary">Submit Rating</button>
          </div>
        )}
      </div>
    </div>
  )
}

export default RideDetails
